import axios from 'axios';
import {useQuery} from '@tanstack/react-query';

const BASE_URL_ORDERS = 'http://127.0.0.1:8000/api/orders/';
const BASE_URL_DRIVERS = 'http://127.0.0.1:8000/api/users/';
const BASE_URL_DRIVER_INFO_BY_TOKEN = 'http://127.0.0.1:8000/api/users/me/';
const BASE_URL_DRIVER_LOGIN = 'http://localhost:8001/api/auth/token/login/';
const BASE_URL_DRIVER_LOGOUT = 'http://localhost:8000/api/auth/token/logout/';


export async function fetchOrders (page = 1) {
  return await axios.get(BASE_URL_ORDERS, {
    params: {
      page: page,
    }
  });
}

export async function submitOrder (data) {
  return await axios.post(BASE_URL_ORDERS, data);
}

export const QueryNewOrders = (time) => useQuery({
  queryFn: () => fetchOrders()
    .then((data) => {
      return data?.data.results
    }),
  queryKey: ["newOrders"],
  refetchInterval: time,
  retry: 5,
  onError: (error) => {alert(error)}
});


export async function submitAddCar (data) {
  return await axios.post(BASE_URL_DRIVERS, data);
}

export async function fetchCars () {
  return await axios.get(BASE_URL_DRIVERS);
}

export const QueryCars = (time) => useQuery({
  queryFn: () => fetchCars()
    .then((response) => {
      return response?.data
    }),
  queryKey: ["cars"],
  refetchInterval: time,
  retry: 5,
  onError: (error) => {alert(error)}
});


export function getDriverInfoByToken (token) {
  return axios.get(BASE_URL_DRIVER_INFO_BY_TOKEN,
    {
      headers: {
        'Content-Type': 'application/json',
        'authorization': `Token ${token}`
      }
    }
  )
}

export function loginQuery(phone, password) {
  return axios.post(BASE_URL_DRIVER_LOGIN,
    {
      phone: phone,
      password: password
    },
    {
      headers : {
        'Content-Type': 'application/json',
      }
    }
  )
}

export function logoutQuery(token) {
  return axios.post(
    BASE_URL_DRIVER_LOGOUT,
    {
      headers: {
        'Content-Type': 'application/json',
        'authorization': `Token ${token}`
      }
    }
  )
}


export function changeDriverDataQuery(token, data, carId) {
  return axios.patch(BASE_URL_DRIVERS + `${carId}/`,
    {
      data
    },
    {
      headers : {
        'Content-Type': 'application/json',
        'authorization': `Token ${token}`
      }
    }
  )
}











