import React, { useState } from 'react';
import styles from './DriverPopup.module.scss';
import {
  cancelOrderByDriver,
  completeOrderByDriver,
  takeOrderByDriver,
} from '../../../services/userService';

const DriverPopup = ({ address, orderId, orderStatus }) => {
  const [isExecutingOrder, setIsExecutingOrder] = useState(false);

  const onClickTakeOrder = (orderId) => {
    return takeOrderByDriver(orderId)
      .then((response) => {
        setIsExecutingOrder(true);
      })
      .catch((err) => {
        alert(err.response.data.error);
      });
  };

  const onClickCancelOrder = (orderId) => {
    return cancelOrderByDriver(orderId)
      .then((response) => {
        setIsExecutingOrder(false);
      })
      .catch((err) => {
        alert(err.response.data.error);
      });
  };

  const onClickCompleteOrder = (orderId) => {
    return completeOrderByDriver(orderId)
      .then((response) => {
        setIsExecutingOrder(false);
      })
      .catch((err) => {
        alert(err.response.data.error);
      });
  };

  return (
    <>
      <div className={styles.driver_popup}>
        <h3>Заказ N{orderId}</h3>
        <p>
          <b>Адрес:</b>
          <br />
          <br /> {address}
        </p>
        <div className={styles.btns}>
          {orderStatus === 'confirmed' ? (
            <button
              onClick={() => onClickCompleteOrder(orderId)}
              className={styles.btn}>
              Завершить заказ
            </button>
          ) : (
            <div className={styles.btns_submit}>
              <button
                onClick={() => onClickTakeOrder(orderId)}
                className={styles.btn}>
                Подтвердить заказ
              </button>
              <button
                onClick={() => onClickCancelOrder(orderId)}
                className={styles.btn}>
                Отклонить
              </button>
            </div>
          )}
        </div>
      </div>
    </>
  );
};

export default DriverPopup;
