import {useEffect, useState} from 'react';
import axios from 'axios'
import styles from "./Login.module.scss";
import {useDriverContext} from '../DriverContext';


function Login({setIsLogin}) {

  const [inputPhone, setInputPhone] = useState('');
  const [inputPassword, setInputPassword] = useState('');

  const {
    carToken,
    setCarToken,
    carId,
    setCarId,
    carNumber,
    setCarNumber,
    driverName,
    setDriverName,
    driverPhone,
    setDriverPhone
  } = useDriverContext();





  const onSuccessLogin = (res) => {
    setCarToken(res?.data?.auth_token);
    setIsLogin(true);

    axios.get(`http://127.0.0.1:8000/api/users/me/`,
      {
        headers : {
          'Content-Type': 'application/json',
          'authorization': `Token ${carToken}`
        }
      }
    )
      .then(res => {
        setCarId(res.data.id);
        setDriverName(res.data.first_name);
        setDriverPhone(res.data.phone);
        setCarNumber(res.data.car_number);
      })
      .then(res => {
        localStorage.setItem('token', carToken);
        localStorage.setItem('car_id', carId);
        localStorage.setItem('driver_name', driverName);
        localStorage.setItem('driver_phone', driverPhone);
        localStorage.setItem('car_number', carNumber);
      })
      .catch((err) => {alert('Не удалось получить данные водителя по токену', err)})

  }

  useEffect(() => {
    setCarToken(localStorage.getItem('token'))

    if (carToken !== '') {
      setCarId(localStorage.getItem('car_id'));
      setDriverName(localStorage.getItem('driver_name'));
      setDriverPhone(localStorage.getItem('driver_phone'));
      setCarNumber(localStorage.getItem('car_number'));
      setIsLogin(true);
    }
  }, [])


  const Login = (e) => {
    e.preventDefault()
    axios.post(`http://localhost:8000/api/auth/token/login/`,
      {
        phone: inputPhone,
        password: inputPassword
      },
      {
        headers : {
          'Content-Type': 'application/json',
        }
      }
    )
      .then(res => {
        res.request.status === 200 ? onSuccessLogin(res) : setIsLogin(false)
      } )
      .catch(err => console.log(err))

  }

  return (

    <section className={styles.login_wrapper}>
      <h2>Вход в личный кабинет</h2>
      <form onSubmit={Login}>
        <input
          type='text'
          onChange={(e) => setInputPhone(e.target.value)}
          value={inputPhone}
          placeholder="Телефон"
        />
        <input
          type="password"
          value={inputPassword}
          onChange={event => setInputPassword(event.target.value)}
          placeholder="Пароль"
        />
        <button onClick={Login} type={'submit'}>Войти</button>
      </form>
    </section>
  )
}

export default Login;
