import React, { useEffect, useState } from 'react';
import styles from './Notice.module.scss';
import cx from 'classnames';
import { useDispatcherContext } from '../DispatcherContext';

// notice - количество заказов со статусом ожидает

const Notice = () => {
  const [notice, setNotice] = useState('');
  const { ordersAll } = useDispatcherContext();

  const handlerOrderFilterByNew = (orders) => orders.status === 'new';
  const ordersNewByFilter = ordersAll.filter(handlerOrderFilterByNew);

  useEffect(() => {
    setNotice(ordersNewByFilter.length);
  }, [ordersNewByFilter]);

  return (
    <>
      <button className={cx(styles.btn, styles.notice)}>
        <svg
          className={cx(
            styles.notice_label,
            notice ? styles.notice_label_alert : '',
          )}
          width="18"
          height="21"
          viewBox="0 0 18 21"
          fill="none"
          xmlns="http://www.w3.org/2000/svg">
          <path
            d="M18 17V18H0V17L2 15V9C2 5.9 4.03 3.17 7 2.29V2C7 1.46957 7.21071 0.960859 7.58579 0.585786C7.96086 0.210714 8.46957 0 9 0C9.53043 0 10.0391 0.210714 10.4142 0.585786C10.7893 0.960859 11 1.46957 11 2V2.29C13.97 3.17 16 5.9 16 9V15L18 17ZM11 19C11 19.5304 10.7893 20.0391 10.4142 20.4142C10.0391 20.7893 9.53043 21 9 21C8.46957 21 7.96086 20.7893 7.58579 20.4142C7.21071 20.0391 7 19.5304 7 19"
            fill="currentColor"
          />
        </svg>
        {notice ? <sub className={styles.notice_count}>{notice}</sub> : ''}
      </button>
    </>
  );
};

export default Notice;
